const e=JSON.parse('{"key":"v-78102dde","path":"/notes/Vue2/10.html","title":"十、watch监视属性","lang":"zh-CN","frontmatter":{"title":"十、watch监视属性","copyright":false,"date":"2022-09-06T00:00:00.000Z","category":["笔记","Vue2"],"tag":["尚硅谷","张天禹"]},"headers":[{"level":2,"title":"（一）监视属性","slug":"一-监视属性","link":"#一-监视属性","children":[{"level":3,"title":"1.监视的两种写法","slug":"_1-监视的两种写法","link":"#_1-监视的两种写法","children":[]}]},{"level":2,"title":"（二）深度监视","slug":"二-深度监视","link":"#二-深度监视","children":[]},{"level":2,"title":"（三）computed 和 watch 的区别","slug":"三-computed-和-watch-的区别","link":"#三-computed-和-watch-的区别","children":[]},{"level":2,"title":"（四）两个重要的小原则","slug":"四-两个重要的小原则","link":"#四-两个重要的小原则","children":[]}],"git":{"createdTime":1662456995000,"updatedTime":1689506602000,"contributors":[{"name":"郁子IKUKO","email":"1147998314@qq.com","commits":2}]},"readingTime":{"minutes":1.21,"words":363},"filePathRelative":"notes/Vue2/10.md","localizedDate":"2022年9月6日","excerpt":"<h2> （一）监视属性</h2>\\n<ul>\\n<li>当被监视的属性变化时，回调函数自动调用，进行相关操作</li>\\n<li>监视的属性必须存在，才能进行监视</li>\\n</ul>\\n<h3> 1.监视的两种写法</h3>\\n<h4> 1）new Vue 时传入 watch 配置</h4>\\n<ul>\\n<li>创建 Vue 实例时已经明确需要监视什么属性</li>\\n</ul>\\n<h4> 2）通过 <code>vm.$watch</code> 监视</h4>\\n<ul>\\n<li>Vue 实例挂载后通过某种行为需要监视某些属性</li>\\n</ul>\\n<h2> （二）深度监视</h2>\\n<ul>\\n<li>Vue 中的 watch 默认不监测对象内部值的改变（一层）</li>\\n<li>配置 <code>deep: true</code> 可以监测对象内部值的改变（多层）</li>\\n</ul>"}');export{e as data};
